css:
@-moz-document domain("userstyles.org") {

/* v1.0.0 by /u/GarethPW */

#header {
    opacity: .9;
    background: rgba(46, 62, 73, 0.9);
    box-shadow: none;
    backdrop-filter: blur(/*[[blur-radius]]*/);
}

#root::before {
    content: '';
    width: 100%;
    height: 68px;
    position: fixed;
    top: 0;
    z-index: 6;
    box-shadow: 0 7px 12px 0 rgba(0, 0, 0, 0.13), 1px 0 0 0 #cfd6d9;
    pointer-events: none;
}

}
