css:
@-moz-document domain("userstyles.org") {
.screenshot_img {
	width: auto;
	height: auto;
	max-width: 220px;
	max-height: 128px;
	border-radius: 0px;
}
.screenshot_wrapper > a > div {
	width: 220px;
	height: 128px;
	display: flex;
	align-items: center;
	justify-content: center;
}
.screenshot_img_long {
	width: auto;
	height: auto;
	max-height: 120px;
	max-width: 185px;
	border-radius: 0px;
	object-fit: contain;
}
.style_info_long {
	margin-left: auto;
}
.style_card_long > div {
	margin-bottom: auto;
}
.style_card_long {
	height: 120px;
	padding: 12px 0px 12px 0px;
}
.style_data_long > a {
	margin-bottom: 20px;
}
/*[[1]]*/ 
}
