css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Change colors of the menus */
menubar, menubutton, menulist, menu, menuitem {
  background-color: rgba(/*[[color1-rgb]]*/, ./*[[menuopacity]]*/) !important; 
  color: /*[[color2]]*/ !important;
}

/* Change color of active tab */
tab[selected="true"] *{
  font-weight: bold !important; 
  background-color: rgba(/*[[color3-rgb]]*/, ./*[[atabopacity]]*/) !important; 
  color: /*[[color4]]*/ !important;
}

/* Change color of non-active tabs */
tab *{  
  background-color: rgba(/*[[color5-rgb]]*/, ./*[[tabopacity]]*/) !important;
  color: /*[[color6]]*/ !important;
}   

/* Change the font style and color of the bookmarks toolbar */
.toolbarbutton-text {
font-size: 8pt !important;
color: /*[[color7]]*/ !important;
font-family: /*[[fonttype]]*/ !important;
}

/* Change color of URL bar */
#urlbar {
  Background-color : /*[[color8]]*/ !important;
  color:  /*[[color9]]*/ !important
}

#urlbar:hover {
  Background-color : /*[[color10]]*/ !important;
  color: /*[[color11]]*/ !important
}

#urlbar[focused="true"] {
  opacity: 1!important
}

/* Change color of search bar */
#searchbar .searchbar-textbox  {
  Background-color : /*[[color12]]*/ !important;
  color:  /*[[color13]]*/ !important
}

#searchbar .searchbar-textbox:hover {
  Background-color : /*[[color14]]*/ !important;
  color:  /*[[color15]]*/ !important
}

#searchbar .searchbar-textbox[focused="true"] {
  opacity:1 !important
} 

/* Change color of URL bar dropdown */
.autocomplete-richlistitem {
  background-color: BG	/*[[color16]]*/ !important; }

.ac-title, .ac-separator, .ac-url, .ac-action {
color: BG	/*[[color17]]*/ !important;
}

/* Change color of URL bar dropdown hover */
.autocomplete-richlistitem[selected=true] {
  background-color: BG	/*[[color18]]*/ !important; 
}

.ac-title[selected=true], .ac-separator[selected], .ac-url[selected=true], .ac-action[selected=true] {
  color: BG	/*[[color19]]*/ !important; 
}

/* Change color of status bar */
.statusbarpanel-text {
  Background-color : /*[[color20]]*/ !important;
  color: /*[[color21]]*/ !important; 
}
