css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url(chrome://browser/content/browser.xul), url(chrome://global/content/customizeToolbar.xul) {
  :root:-moz-lwtheme-brighttext {
    --chrome-background-color: #272b35;
    --chrome-color: #f8f8f8;
    --chrome-secondary-background-color: #393f4c;
    --chrome-navigator-toolbox-separator-color: rgba(0, 0, 0, 0.2);
    --chrome-nav-bar-separator-color: rgba(0, 0, 0, 0.2);
    --chrome-nav-buttons-background: #252c33;
    --chrome-nav-buttons-hover-background: #1b2127;
    --chrome-nav-bar-controls-border-color: #1d2328;
    --chrome-selection-color: #fff;
    --chrome-selection-background-color: /*[[color]]*/;
    --tabs-toolbar-color: #f8f8f8;
    --tab-background-color: #272b35;
    --tab-hover-background-color: #07090a;
    --tab-selection-color: #f8f8f8;
    --tab-selection-background-color: /*[[color]]*/;
    --tab-selection-box-shadow: none;
    --pinned-tab-glow: radial-gradient(22px at center calc(100% - 2px), /*[[color]]*/ 13%, rgba(0, 0, 0, 0.4) 16%, transparent 70%);
    --url-and-searchbar-background-color: #171b1f;
    --urlbar-separator-color: #5f6670; }
  :root:-moz-lwtheme-brighttext,
  toolbar:-moz-lwtheme-brighttext {
    --toolbarbutton-hover-background: rgba(25, 33, 38, 0.6) linear-gradient(rgba(25, 33, 38, 0.6), rgba(25, 33, 38, 0.6)) padding-box;
    --toolbarbutton-hover-boxshadow: none;
    --toolbarbutton-hover-bordercolor: rgba(25, 33, 38, 0.6);
    --toolbarbutton-active-background: #192126 linear-gradient(#192126, #192126) border-box;
    --toolbarbutton-active-boxshadow: none;
    --toolbarbutton-active-bordercolor: rgba(25, 33, 38, 0.8);
    --toolbarbutton-checkedhover-backgroundcolor: /*[[color]]*/; }
  :root:-moz-lwtheme-darktext {
    --url-and-searchbar-background-color: #fff;
    --chrome-background-color: #E3E4E6;
    --chrome-color: #18191a;
    --chrome-secondary-background-color: #f5f6f7;
    --chrome-navigator-toolbox-separator-color: #cccccc;
    --chrome-nav-bar-separator-color: #B6B6B8;
    --chrome-nav-buttons-background: #fff;
    --chrome-nav-buttons-hover-background: #DADBDB;
    --chrome-nav-bar-controls-border-color: #ccc;
    --chrome-selection-color: #f8f8f8;
    --chrome-selection-background-color: /*[[color]]*/;
    --tab-background-color: #E3E4E6;
    --tab-hover-background-color: #D7D8DA;
    --tab-selection-color: #f8f8f8;
    --tab-selection-background-color: /*[[color]]*/;
    --tab-selection-box-shadow: none;
    --pinned-tab-glow: radial-gradient(22px at center calc(100% - 2px), /*[[color]]*/ 13%, transparent 16%); }
  :root:-moz-lwtheme-darktext,
  toolbar:-moz-lwtheme-darktext {
    --toolbarbutton-hover-background: #eaeaea;
    --toolbarbutton-hover-boxshadow: none;
    --toolbarbutton-hover-bordercolor: rgba(0, 0, 0, 0.1);
    --toolbarbutton-active-background: #d7d7d8 border-box;
    --toolbarbutton-active-boxshadow: none;
    --toolbarbutton-active-bordercolor: rgba(0, 0, 0, 0.15);
    --toolbarbutton-checkedhover-backgroundcolor: #d7d7d8; } }
/*# sourceMappingURL=firefox.css.map */
