}

.LiveAvatar-column {
	width: calc(50%)
}

.LiveAvatar-column+.LiveAvatar-column {
	border-left: 0
}

.LiveAvatar-img.alignCenter {
	border-radius: 0
}




/* 主栏阴影 */
.Card.TopSearch.TopSearch--new {
	box-shadow: 0 1px 3px rgba(26, 26, 26, 0.1)
}
  
  /* 热点 */
