	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none
}

/* 最新讨论 */
.TimeBox-MainContent .Card.SearchResult-Card {
	-webkit-box-shadow: none;
	-moz-box-shadow: none;
	box-shadow: none
}
/* 主栏阴影 */

/* 边栏阴影 */
.Card.SearchResult-Card {
	box-shadow: 0 1px 3px rgba(26, 26, 26, 0.1)
}
/* 边栏阴影 */


