css:
@-moz-document domain("bilibili.com") {
.v-title h1 {
    height: auto !important;
    white-space: normal !important;
}

#viewbox_report h1 {
    height: auto !important;
    white-space: normal !important;
}

.dynamic-m .dyn_list_wrapper .dyn_list li .r .info a {
    white-space: normal !important;
}
.dynamic-m .dyn_list_wrapper .dyn_list li .r .info {
    height: auto !important;
}

.up-info .u-info .desc {
    line-height: 18px;
    height: auto;
    white-space: normal;
    margin-bottom: 4px;
}

.bilibili-player-promote-wrap,
.ad-f,
.ad-e1,
.ad-e2,
.share-tool-bar,
#app_qrcode_box,
.video-top-ad,
.video-bottom-ad,
#playpage_mobileshow {
    display: none !important;
}

.video-desc .info {
    height: auto !important;
}

[report-id="abstract_spread"],
[report-id="abstract_unspread"] {
    display: none !important;
}
}
