#rbx-body {
    background-image: url("http://i.imgur.com/DC5iqlb.png");
    background-size: 250px 250px;
}

.content {
    background: rgba(0,0,0,0);
}

a, h3 {
    color: white !important;
    text-shadow: 0px 0px 2px rgba(0, 0, 0, 1);
}

div.game-card-container, .btn-secondary-md, .notification-blue, .btn-secondary-xs, .btn-primary-md {
    background-color: #004 !important;
}

div.section-content {
    background-color: #ef0000;
}

#header {
    background-color: #ef0000;
}
}
