css:
/* Mew's YT v1.0.0 */

@-moz-document domain("youtube.com") {

:root {
  --bgn: /*[[bgn]]*/; /* main bg color */
  --bgd: /*[[bgd]]*/; /* darker highlight color */
  --bgh: /*[[bgh]]*/; /* lighter highlight color */
  --bgt: #b6babf; /* faded text color */
  --bgo: #e9f4fe; /* text color */
  --bga: #00ff7f; /* accent color */
}

/* main background */
ytd-app, ytd-multi-page-menu-renderer, ytd-account-settings {
  background: var(--bgn) !important; }

/* highlighted elements */
ytd-masthead, #guide-content, ytd-active-account-header-renderer, #channel-header.ytd-c4-tabbed-header-renderer, #tabs-inner-container, .header.ytd-playlist-panel-renderer, .playlist-items.ytd-playlist-panel-renderer, ytd-alert-renderer div {
  background: var(--bgh) !important; }

/* darkened elements */
ytd-author-comment-badge-renderer {
  background: var(--bgd) !important; }
  
/* text color */
yt-formatted-string {
  color: var(--bgt) !important; }

/* buttons */
paper-button.ytd-subscribe-button-renderer, #submit-button {
  color: var(--bgt) !important;
  background: var(--bgd) !important; }
  /* text color */
  paper-button.ytd-subscribe-button-renderer * {
  color: var(--bgt) !important; }

/* notifications (not fully tested) */
paper-toast#toast {
  background: var(--bgh) !important; }

/* popout menus */
paper-menu {
  background: var(--bgh) !important; }
  paper-menu div .iron-selected, paper-menu div a:hover, ytd-menu-service-item-renderer:hover {
  background: var(--bgd) !important; }

/* search box */
ytd-searchbox {
  justify-content: center !important; }
  /* input bar background */
  .ytd-searchbox {
    border: none !important;
    background: var(--bgd) !important; }
    /* search results */
    .gstl_50.sbdd_a * {
      border-color: var(--bgh) !important;
      color: var(--bgt) !important;
      background: var(--bgh) !important; }
  /* hover bg change */
  .sbsb_c.gsfs:hover, .sbsb_c.gsfs:hover *, #items.iron-list > *:hover {
    background: var(--bgd) !important; }

/* fun with avatars */
#avatar > img {
  transition: 200ms ease-in-out !important; }
#avatar > img:hover {
  transform: rotateZ(9deg) !important; }


}
