css:
@-moz-document domain("open.spotify.com") {
nav.navBar {
    background: #eee !important;
}
.JKKBXFILp09eIN4WugwgY, .tracklist-row:focus, .inputBox, ._1wLr_CVCgF5x-vj1F2EkS7 {
    background: url(http://imgh.us/Minimalist+Sunset.jpg) no-repeat !important;
}
.now-playing-bar {
    background: #ddd !important;
}
.tracklist-row .play-pause, .track-name, .position, .tracklist-row .tracklist-col.name .track-name-wrapper .artists-album a, .total-duration, .btn, a, body, .recently-played .navlist .navlist-item .navlist-itemlink .type, .inputBox-input {
    color: #444444 !important;
}
.tracklist-row
.artists-album-separator {
    color: #ccc !important;
}
.tracklist-row {
    transition: 0s !important;
}
.tracklist-row:hover {
    background-color: #eee !important;
    outline: 1px solid #ccc !important;
}
.progress-bar__fg {
    background-color: #E65454 !important;
}
.control-button--active, .tracklist-row.active .duration, .tracklist-row.active .play-pause, .tracklist-row.active .position, .tracklist-row.active .track-name {
    color: #E65454 !important;
}
.navBar .group {
    border-top: 1px solid  !important;
}
.ads-container {
    background-color: transparent !important;
    border-top: 1px solid transparent !important;
}
.inputBox-input {
    border-bottom: 1px solid #ccc !important;
}
.btn.btn-black {
    background: transparent !important;
    border: 2px solid #333 !important;
}
.btn.btn-green {
    background-color: #2196F3 !important;
    color: #fff !important;
}
._2rScaV3bBHoj7O47a_JnaH:after, ._2rScaV3bBHoj7O47a_JnaH:hover:after {
    background-color: #2196F3 !important;
}
.connect-bar {
    background-color: #2196F3 !important;
}
.connect-bar:after {
    border-bottom: 10px solid #2196F3 !important;
}
.control-button:hover {
    color: #000 !important;
}
}
