css:
@-moz-document domain("lifehacker.co.uk"){


  body, .site-content, .meta__full, .meta--line:before, .meta h2, .meta h4, .meta a, .meta__limited, .future-company-footer, .site-container__header.group, .site-footer, .media.media--sponsored {
    background: #222222 !important;
  }
  
  .share-block, .textarea-wrapper:before {
    background: #808080 !important;
  }
  
  .btn.pagination--helper.pagination--helper--next {
    background: #303030 !important;
  }
  
  .single-article .post-title, a {
    color: #909090 !important;
  }
  
  .single-article__content, p {
    color: #808080 !important;
  }
  
  a:hover, a:visited:hover {
	 color: #89a73d !important;
  }
  
  a:focus {
	 outline: dotted thin !important;
	 color: #6b8130 !important;
  }
  
  .share-block__item__element.share-block__item__element--link a {
    color: #333333 !important;
  }

  .single-article__content blockquote {
	background: #262626;
  }

  .single-article__content h3 {
        color: #fff;
  }

  .share-block, .textarea-wrapper::before {
	background: #262626 !important;
  }

  body {
	color: #565656;
  }
}
