css:
@font-face {
    font-family: 'Fira Code';
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Light.eot');
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Light.eot') format('embedded-opentype'), url('https://unpkg.com/firacode/distr/woff2/FiraCode-Light.woff2') format('woff2'), url('https://unpkg.com/firacode/distr/woff/FiraCode-Light.woff') format('woff'), url('https://unpkg.com/firacode/distr/ttf/FiraCode-Light.ttf') format('truetype');
    font-weight: 300;
    font-style: normal;
}
@font-face {
    font-family: 'Fira Code';
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Regular.eot');
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Regular.eot') format('embedded-opentype'), url('https://unpkg.com/firacode/distr/woff2/FiraCode-Regular.woff2') format('woff2'), url('https://unpkg.com/firacode/distr/woff/FiraCode-Regular.woff') format('woff'), url('https://unpkg.com/firacode/distr/ttf/FiraCode-Regular.ttf') format('truetype');
    font-weight: 400;
    font-style: normal;
}
@font-face {
    font-family: 'Fira Code';
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Medium.eot');
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Medium.eot') format('embedded-opentype'), url('https://unpkg.com/firacode/distr/woff2/FiraCode-Medium.woff2') format('woff2'), url('https://unpkg.com/firacode/distr/woff/FiraCode-Medium.woff') format('woff'), url('https://unpkg.com/firacode/distr/ttf/FiraCode-Medium.ttf') format('truetype');
    font-weight: 500;
    font-style: normal;
}
@font-face {
    font-family: 'Fira Code';
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Bold.eot');
    src: url('https://unpkg.com/firacode/distr/eot/FiraCode-Bold.eot') format('embedded-opentype'), url('https://unpkg.com/firacode/distr/woff2/FiraCode-Bold.woff2') format('woff2'), url('https://unpkg.com/firacode/distr/woff/FiraCode-Bold.woff') format('woff'), url('https://unpkg.com/firacode/distr/ttf/FiraCode-Bold.ttf') format('truetype');
    font-weight: 700;
    font-style: normal;
}
/*[[tags]]*/ {
  font-family: "/*[[font]]*/", monospace !important;
  font-variant-ligatures: normal !important;
}
