css:
@-moz-document  domain('chan.sankakucomplex.com'),
                domain('gelbooru.com'),
                domain('konachan.com'),
                domain('yande.re'),
                domain('danbooru.donmai.us'),
                url(https://chan.sankakucomplexxx.com/) {
  
  /*********************/
  /* Booru Mascot v1.0 */
  /*********************/
  
  :root {
      /* Align */
    /*[[align]]*/
      /* Size */
    --mascotw: /*[[mwidth]]*/;
    --mascoth: /*[[mheight]]*/;
      /* Offset */
    --voffset: /*[[voffset]]*/;
    --hoffset: /*[[hoffset]]*/;
      /* Opacity */
    --opacity: /*[[opacity]]*/;
      /* Transform */
    --flip-x: /*[[flip]]*/;
      /* Filter */
    --grayscale: /*[[grayscale]]*/;
      /* Mascot */
    --mascot: url(/*[[mascot]]*/);
  }
  
  body:before {
    content: "";
    background-image: var(--mascot) !important;
    background-repeat: no-repeat;
    background-position: var(--align-bp) bottom;
    background-size: var(--mascotw) var(--mascoth);
    
    /* Align */
    right: var(--align-r);
    left: var(--align-l);
    bottom: 0;
    
    /* Size */
    width: var(--mascotw);
    height: 100%;
    
    /* Position */
    margin-bottom: var(--voffset);
    margin-right: var(--margin-r);
    margin-left: var(--margin-l);
    position: fixed;
    
    /* Opacity */
    opacity: var(--opacity);

    /* Transform */
    transform: var(--flip-x);
    
    /* Filter */
    filter: var(--grayscale);
    z-index: -1;   
  }

}

@-moz-document domain('danbooru.donmai.us') {
  
  /* Remove the white background on Danbooru */
  #page, #top, #page-footer {
    background-color: transparent !important;
}
    
}
