css:
@-moz-document url-prefix(http://www.eldarya.)
{

/*ИКОНКА*/
#pet-headerPortrait #pet-headerPortrait-base {
   background: url("/*[[css1]]*/") no-repeat !important;
   -webkit-filter: hue-rotate(0deg);
   filter: hue-rotate(0deg);
}  

/*В ПРОФИЛЕ ГГ*/
#pet-portrait > img[src*="/static/img/pet/icon/"] {display : none !important ;}
#player-display-pet {
    height:  /*[[css5]]*/px!important ;
    width:  /*[[css6]]*/px!important ;
}  #player-display-pet > img[src*="/static/img/pet/mood/profile/"] {opacity : 0 !important ; }
   #player-display-pet {
    background : url("/*[[css2]]*/") center no-repeat !important ;
    background-size: contain !important;
} 
/*НА СТРАНИЦЕ РАЗВЕДКИ*/ 
   #pet-portrait {
    background : url("/*[[css2]]*/"), url("/*[[css3]]*/") !important ;
    background-size: contain !important;
    background-position: bottom !important;
    background-repeat: no-repeat !important;
    height : 450px !important ;
    width: 450px !important ;
}   
  
/*ПОРОДА ФАМИЛЬЯРА*/
 #pet-info-racegender:before {
    content : "/*[[css4]]*/" !important ;
    font-size : 15px !important ;
    line-height : 10px !important ;
}  
#pet-info-racegender {
    font-size : 0 !important ;
    margin-top : 5px ;
}  




/*ИМЯ*/

#pet-info-name {
    font-size: 0px;
}

#pet-info-name:before {
    content: "/*[[font]]*/" !important;
    font-size: 20px !important;
    line-height: 16px !important;
}


/*ЕДА*/
#pet-info-food > img {
  background : url("/*[[css8]]*/") !important ;
  background-size : 72px 72px !important ;
  margin-top : 0px !important ;
  padding-left : 72px !important ;
  padding-top : 72px !important ;
  width : 0 !important ;
  height : 0 !important ;    
  border-radius: 50px;
  border: 1px solid;
} 
#pet-info-food {
  font-size : 0 !important;}    
#pet-info-food > span {
  font-size : 15px !important;
}
}
