css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.yahoo.com") {
/* Sidebar rework */
[data-test-id="right-rail-ad"] {
    display: none !important;
}
[data-test-id="mail-left-rail"] {
    min-width: 150px !important;
    width: 150px !important;
}
[data-test-id="comms-properties-bar"] {
    justify-content: flex-start !important;
    flex-direction: column !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
}

[data-test-id="comms-properties"] > A:nth-child(1n+0) {
    margin-right: 0px !important;
}

[data-test-id="mim-launcher"] {
    margin-right: 0px !important;
}

[data-test-id="settings-link-label"] {
    display: none !important;
}

[data-test-id="comms-properties"] {
    flex-direction: column !important;
}

[data-test-id="mail-right-rail"] {
    flex-direction: row !important;
    flex-wrap: wrap-reverse !important;
    width: 30px !important;
}

/* Shrink Mail List */
[data-test-id="mail-reader-list-container"] > [data-test-id="left-pane"] {
    flex-basis: 27% !important;
}

/* Hide Ad in Navigation */
[data-yaft-module="norrin_ad_tl1"] {
    display: none !important;
}
[data-yaft-module="norrin_ad_rec"] {
    display: none !important;
}

/* Hide Ad placed in mailbox */
[data-test-id="video-container"] {
    display: none !important;
}

[data-test-id="infinite-scroll-PENCIL"], [data-test-id="ad-viewability-tracker"]  {
    display: none !important;
}
[data-test-id="infinite-scroll-content"] {
    margin-top: -40px !important;
}

/* Hide "Give Feedback" button */
[data-test-id="panel-stack"] {
    display: none !important;
}

/* Shrink Headder */
header.norrin-ybar > [role="banner"],
header.norrin-ybar > [role="banner"] > div:nth-child(2),
header.norrin-ybar > [role="banner"] > div:nth-child(2) > div {
    height: 55px !important;
}

header.norrin-ybar > [role="banner"] > div:nth-child(2) > div > div:nth-child(2),
header.norrin-ybar > [role="banner"] > div:nth-child(2) > div > div:nth-child(3) {
    margin-top: -30px !important;
}

[data-test-id="mail-app"] > [role="banner"] > div:nth-child(1) {
    height: 55px !important;
}

div.W_3vpWV.d_3qWeB.H_6D6F.D_F.ek_BB.em_N.I_cOYF6.A_6EqO {
    position: absolute !important;
    margin-left: -300px !important;
    margin-top: -14px !important;
    z-index: 99 !important;
}

[data-test-id="modal-outer"] {
    display: none !important;
}
}
