css:
@-moz-document domain("homebrewery.naturalcrit.com") {
element.style {}
/* Main BG color and normal text color */
.CodeMirror {
    background: #293134;
    color: #91A6AA;
}

/* Brew BG */
.brewRenderer {
    background-color: #293134;
}

/* Blinking cursor */
.CodeMirror-cursor {
    border-left: 1px solid #e0e2e4;
}

/* Bars at the top */
.snippetBar {
    background-color: #2F393C;
    color: white;
}
nav {
    background-color: #293134;
}
nav .navItem {
    background-color: #293134;
}
.snippetBar .snippetGroup .dropdown {
    background-color: #2F393C;
}

/* Line number stuff */
.CodeMirror-gutter-elt {
    color: #81969A;
}
.CodeMirror-linenumber {
    background-color: #293134;
}
.CodeMirror-gutter {
    background-color: #293134;
}

/* Colors for headings and such */
.cm-s-default .cm-header {
    color: #678CE1; /* ###Headings */
}
.cm-s-default .cm-link {
    color: #678CB1; /* Links */
}
.cm-s-default .cm-string {
    color: #EC7600; /* Red stuff? */
}
.cm-s-default .cm-quote {
    color: #93C763; /* Green */
}
.cm-s-default .cm-variable-2 {
    color: #639733; /* Bullets and such */
}
.cm-s-default .cm-attribute {
    color: #678CB1; /* Stuff inside div tags */
}
.cm-s-default .cm-tag {
    color: #93C763; /* Tags */
}
.editor .codeEditor .pageLine { /* New page. Credit goes to /u/thefinalslowdance ! */
    background-color: rgba(0,0,0,0.3);
    border-bottom: 1px solid rgba(0,0,0,0.3);
}
}
