css:
@-moz-document domain("liquipedia.net") {

div {
    background-color: #1d1f21 !important;
    color: #c5c8c6 !important;
}

.match-filler, .tournaments-list-heading, tr, td, table, th, .nav li, #toc ul, .infobox-header {
	background-color: #282a2e !important;
}

.fo-nttax-infobox>div, .infobox-cell-2, .infobox-center, li.active, blockquote {
	background-color: #2f3135 !important;
}
a, a:visited {
	text-decoration: none;
    color: lightblue !important;
    background: none;
}
a:hover, li>a:hover {
 	text-decoration: none;
    color: #337ab7 !important;
    background: none !important;
}
a.new {
  color: pink !important;
}
.nav-tabs>li {
  border: none !important;
  height: 100%;
}
.nav-tabs > li > strong, .nav-tabs > li.active > a, #sidebar-toc ul li.active > a {
    background-color: #4d5158;
    color: #c5c8c6;
}
  
.nav-tabs > li > a.mw-selflink.selflink {
      background-color: #303c56 !important;
}
::-webkit-scrollbar {
  width: 10px;
}
::-webkit-scrollbar-track {
  background: #444; 
}
::-webkit-scrollbar-thumb {
  background: #222; 
}
::-webkit-scrollbar-thumb:hover {
  background: #131313; 
}
}
