@namespace url(http://www.w3.org/1999/xhtml);

/*

  Force compact for Google News New Design since June 2017

  Author: backy0175
  Date: 2017/07/09

*/

@-moz-document domain("news.google.com") {

/*[[OneColumn]]*/
  
  
/*[[HideThumbnail]]*/
  

/*[[ThumbnailSize]]*/
  
  /* adjust paddings and margins */
  .lPV2Xe 
  {padding: 8px !important;
  padding-top: 0px !important;}
  
  .fkWPz,
  .M1Uqc,
  .zaeuPd
  {margin-top: 8px !important;}


/*[[HideRelatedCoverag]]*/


  /* resize menu */
  /* .gb_me */
  .gb_le
  {width: 16% !important;}
  .zQTmif
  {margin-left: 15% !important;}
  .uyYuVb 
  {font-size: 12px !important;}
  .v4IxVd
  {width: 100% !important}


  /* Adjust Margins */
  .M1Uqc,
  .O0WRkf,
  .zaeuPd,
  .oyJxcb
  {margin-top: 4px !important;}
  
  .tgGNL
  {margin-bottom: 4px !important;
  margin-top: 8px !important;}
  .WyeMbd
  {margin-bottom: 16px !important;}
  .lPV2Xe
  {padding-bottom: 4px !important;}
  

  /* Adjust Font Size */
  .adH5zf
  {font-size: 20px !important;}
  
  /*[[FontSizeBody]]*/
 
  /* 2017/10/12 - fix menu width issue */
  .gb_dc:not(.gb_9b)
  {width: 16% !important;}
  
  .JQZRxb
  {width: 84% !important;}

}
