css:
@-moz-document domain("www.btbtt.co") {
/* 去廣告 */
.width.imgs_1,.bdshare-slide-button-box.bdshare-slide-style-r0,#footer,.error,div.width:nth-child(3){
	display:none !important;
}
body{
	background:#FFF !important;
}
}

@-moz-document domain("www.btbtt.co") {
/* 美化 */
div.div div.header {
    padding: 6px;
    border-bottom: 0px solid #C8C8C8;
    font-weight: 400;
    height: auto;
    line-height: 16px;
    overflow: hidden;
    text-shadow: 0 0 0 #FFFFFF;
    font-size: 16px;
	background-color: #eeeeee;
}
div.div{
	background:#FFF !important;
}
div.div div.header,
#nav td{
	background: #eeeeee !important;
}
/* 頁面選擇器 */
.page a, .page a.checked{
	background: #736969 !important;
}
/* 
	清單
*/
/* 去除新窗口打開的 Icon */
a[title="点击图标，新窗口打开"]{display:none !important;}
/* 去除回復、查看欄 */
.header>table>tbody>tr>td[class="views"],
.body.threadlist>table>tbody>tr>td[class="views"]{
	display:none !important;
}
}
