css:
@-moz-document domain("www.gyakorikerdesek.hu") {
html, body, table, td, th, p {
    font-family: "Segoe UI", Calibri, Helvetica, Arial, sans-serif;
}

a {
    text-decoration: none;
    transition: 0.2s ease all;
}
a:hover {
    color: #f1f1f1;
}
a.gomb:hover {
    color: #212121
}

body,
table,
.onreklam,
.sor,
.valaszok span {
    background: #212121;
    background-color: #212121 !important;
    color: #FFF;
    border: 0;
}
a {
    color: #039BE6;
}
* {
    border-color: #303030 !important;
}
#img_sajatmenufelul,
#img_menualul,
#img_menufelul,
#img_temamenufelul {
    display: none !important;
}

.jobb_oldal .width100:first-child { display: none !important; }

iframe { display: none !important; }

.jobb_oldal > br,
.jobb_oldal > div.center,
.img_menualul {
    display: none !important;
}

}
