css:
@-moz-document url-prefix("https://lightning.bitflyer.jp/"), url-prefix("https://lightning.bitflyer.com/") {
@media screen and (min-width: 1824px) {
    .open-orders .pane__orders.pane__orders--btcfx .status__orders--avesum,.open-orders .pane__fx.pane__orders--btcfx .status__orders--avesum {
        top: 36px;
    }
}

body, main {
    overflow: none !important;
}

main {
    overflow-y: scroll !important;
}

.board .board__container .orderbook__offers li,.board .board__container .orderbook__bids li {
    font-size: 12pt !important;
    line-height: .7em;
}

.execution-history .status__table--history {
    font-size: 11pt;
    line-height: .2em;
}

.order .pnl__list .pnl__period,.order .pnl__list .pnl__funds,.order .pnl__list .pnl__collateral {
    font-size: 10pt;
    line-height: .8em;
}

.market-history__list {
    line-height: .75em;
}

.open-orders .status__table--orders,.open-orders .status__table--fx, .open-orders .status__table__head--orders {
    font-size: 10pt;
    line-height: .3em
}

.wrapper--trade .main--top {
    height:89% !important;
    max-height: none;
}
}
