css:
@-moz-document url-prefix("https://www.pinterest.") {
:not([data-grid-item="true"]) {
    visibility: hidden;
}
.BoardPinGrid div[data-grid-item="true"]:first-of-type {
    display: none;
}
[data-grid-item="true"] * {
    visibility: visible
}
button {
    display: none;
}
h3 {
    visibility: visible !important;
}
h3.richPinGridTitle {
    display: none;
}
.AddPinRepWrapper {
    display: none;
}
.pinCredits {
    display: none !important;
}
.pinDpSocialCounts {
    display: none;
}
.searchRelatedQueries {
    display: none;
}
.pinWrapper > *:not(:first-child) {
    display: none;
}
}
