css:
@-moz-document domain("tweetdeck.twitter.com") {
.column-scroller {
    overflow-y: hidden;
}

::-webkit-scrollbar {
    opacity: 0 !important;
    background-color: #222426 !important;
}

section.js-column.column.column-type-home.will-animate {
    width: /*[[home-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-interactions.will-animate {
    width: /*[[noti-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-tweets.will-animate {
    width: /*[[user-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-message.will-animate {
    width: /*[[mess-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-list.will-animate {
    width: /*[[list-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-customtimeline.will-animate {
