    /*[[resizer]]*/
}

section.js-column.column.column-type-new-followers.will-animate {
    width: /*[[followers-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-scheduled.will-animate {
    width: /*[[scheduled-width]]*/px;
    /*[[resizer]]*/
}

section.js-column.column.column-type-search.will-animate {
    width: /*[[trending-width]]*/px;
    /*[[resizer]]*/
}
}
