css:
@-moz-document domain("southpark.cc.com") {
  
:root{
--Greyish-Color: rgba(60,70, 50, 0.2);
--BG-Image: url(https://image.ibb.co/dp4QhF/concrete_2564978_1920.jpg) ;
--Random-Image: url(http://cdn.jsdelivr.net/emojione/assets/png/1f3b2.png?v=2.2.5);
}

html {
color: rgba(110, 110, 110, 1) !important;
}
	
body,body[data-theme="white-on-black"] {
background-image: var(--BG-Image);
background-repeat: round;
	
}
	
.actions, .more-info, .module, .grid-container, .wiki-entry {
background-color: rgba(100, 100, 100, 0) !important;
}
		
#main {
transform: scale(1.02) !important;
}	
	
section.content > section > div {
background-image: var(--BG-Image);
background-repeat: round;
width: 100%;
}
	
section.filters > nav > * {
width: -moz-fit-content;
margin-right: 2px;
}

section.content, #container {
margin-left: auto;
width: -moz-fit-content;
}
	
section.page-content.with-dark-hero > div > .hero .episodesHero.active, .main-sub-nav .container {
background-image: var(--BG-Image);
}
  
section.videos.thumbnails li > a > div.copy, section.videos.images li > a > div.copy {
background-color: rgba(48, 48, 48, 0.75);
color: #A8A8A8 !important;
}

a:hover, a:link, a:visited, a:active {
text-decoration: none;
color: rgba(140, 140, 140, 1);
}
section.page-content > div.videos-page .view-gradient {
position: absolute;
z-index: 0;
margin-top: -20px;
height: 100px;
width: 100%;
background-image: -webkit-gradient(linear, right top, right bottom, color-stop(0, #000000), color-stop(0.12, #000000), color-stop(0.5, #000000));
}
  
section.videos.thumbnails li > a > div.copy, section.videos.images li > a > div.copy {
padding: 7px 10px 0 10px;
background-color: transparent;
height: 43px;
border: 1px solid #ccc;
border-top: 0;
border-left-color: var(--Greyish-Color);
border-bottom-color: var(--Greyish-Color);
border-right-color: var(--Greyish-Color);
}
	
section.page-content.home > div > section.module.special, section.blog .module .arrow.left, section.blog.module .arrow.left {
background-color: transparent;
}
	
section.carousel > div.carousel-wrapper.elastislide-wrapper > nav > span {
color: #8e9124 !important;
}
	
section.videos.thumbnails li.featured > a > div.copy, section.videos.images li.featured > a > div.copy {
background-color: rgba(50, 60, 40, 0.09);
border-bottom-left-radius: 48px;
border-bottom-right-radius: 5px;
}
	
.select-wrapper, .select-chevron, icon-chevron-down, .select-style {
background-color: rgba(150,150,150,.0) !important;
background: -moz-linear-gradient(top, rgba(120,125,100,0.6) 0%, rgba(120,125,90,0.6) 26%, rgba(25,25,25,0.6) 100%) !important;
border: 0px solid #000 !important;
border-radius: 50px 0px 20px 2px;
}
#sort option {
background-color: rgba(120,125,100, 1) !important;
text-align: -moz-center !important;
}	
	
.buttons > *, .active:not(.episodesHero):not(.arrow):not(.episodes) {
background-image: none !important;
background: rgba(71,71,71, 0.5) !important;
}	
	
.select-style > * > * > * {
background: -moz-linear-gradient(top, rgba(120,125,100,0.9) 0%, rgba(120,125,90,0.9) 26%, rgba(25,25,25,0.9) 100%) !important;
text-align: center !important;
}	
	
.select-chevron::before {
color: #8e9124 !important;
}	

.view-gradient {
background-image: none !important;
}
a.random-episode {
background-image: var(--Random-Image);
transform: scale(1);
filter: blur(0px) saturate(.65);
opacity: 0.8;
}
a.random-episode:hover {
background-image: var(--Random-Image);
transform: scale(1.6);
filter: blur(0);
opacity: 1;
}
	
section.carousel.wiki li:nth-child(6n+1), section.carousel.wiki li:nth-child(6n+2), section.carousel.wiki li:nth-child(6n+3)    {
background-color: rgba(0, 0, 0, 0);
}
	
section.content > * {
height: 110% !important;
}	
	
section.page-content.home > div > section.module.special, section.content > footer .footer, section#main_content > footer  {
display: none;
}

}
