css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
  .promoted-tweet:not(.descendant) {
    background-color: /*[[promoted-default]]*/;
  }
  
  .promoted-tweet:not(.descendant):hover {
    background-color: /*[[promoted-hover]]*/;
  }

  .promoted-account {
    background-color: /*[[promoted-account]]*/;
  }

  .promoted-trend {
    background-color: /*[[promoted-trend]]*/;
  }
}
