@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pixiv.net") {
  ._user-profile-card {
    padding: 2px 16px 0;
  }
  ._user-profile-card ._user-icon.size-80 {
    width: 170px;
    height: 170px;
    background-size: inherit;
  }
  ._user-profile-card ._user-icon {
    background-color: transparent;
    border-radius: 3px 3px 0 0;
  }
  ._user-profile-card ._user-icon[style*="/common/images/no_profile"] {
    background-size: 100%;
  }
  ._user-profile-card ._user-icon.cover-texture:before {
    background-color: transparent;
  }
  /*[[menu_dot_visibility]]*/
}
