css:
@-moz-document url-prefix("https://outlook.live.com/mail/") {
/* Right-side ad banner */
div#app > div > div:nth-child(3) > div > div > div:nth-child(4) {
    display: none !important;
}

/* Bottom ad banner */
div#app > div > div:nth-child(3) > div > div:nth-child(2) {
    display: none !important;
}

/* "Upgrade" banner */
div#app > div > div:nth-child(3) > div > div:nth-child(1) > div:nth-child(1) > div:nth-child(2) > div:nth-child(1) > div:nth-child(2) {
    display: none !important;
}
}
