css:
@-moz-document domain("kadu.ru"), domain("hlamer.ru"), domain("smotri.pro") {

:root
{
	--color1: #303030; /*Задний фон*/
	--color2: #373b3e; /*первый слой*/ 
	--color3: #444444; /*второй слой*/
	--color4: black; /*меню*/
}
/*[[Style]]*/

ul#nav, div#nav-sub, #nav, #nav-sub,  ul#channel-category-line .selected, #user-bar, ul.thread.dialog-list .unread
{
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
}
div.block-side, div.block-side-extra, .select2-drop-active, .mejs-contextmenu
{
    border: 1px dotted #999eb1;
    border-radius: 10px;
}


div#ads-top
{
    opacity: 0.7;
}

div#video-line-content
{
    background: url(http://i.imgur.com/q7Cz930.png);
    background-size: 140px 70px;
    
    padding: 10px 0px;
    width: max-content;
}
div#align
{
    padding: 0px 0px 1000px 0px;
}
object#video-flash
{
    display: none;
}
/*
.mejs-mediaelement
{
    position: relative;
}
div#html5-container.vfc-wide {
    height: auto !important;
}
.mejs-container
{
    overflow: visible
}*/
div#video-flash-container :nth-last-child(1)
{
    display: block !important;
}

.vfc-wide
{
    height:489px !important;/*height: 800px;*/
}



body::-webkit-scrollbar, #dialog-container::-webkit-scrollbar, .select2-results::-webkit-scrollbar, textarea::-webkit-scrollbar
{
    /*background-color: var(--color3);*/
      background-color: rgba(20, 20, 20, 0.1);
    -webkit-box-shadow: inset 0 0 20px rgba(0,0,0,0.5);
    /*box-shadow: 0 0 0 2000px rgba(0, 0, 0, 0.30) inset; */
}

body::-webkit-scrollbar-thumb ,#dialog-container::-webkit-scrollbar-thumb, .select2-results::-webkit-scrollbar-thumb, textarea::-webkit-scrollbar-thumb
{
  background-color: rgba(255, 255, 255, 0.5);;/*#2468a2;*/
    border-radius: 20px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3); 
}

#scrollup
{
    top: 0px;
    left:0;
    height: 40px;
    z-index: 0;
        padding: 60px 20px 100% 20px;
    transition: all 0.2s ease;

}
#scrollup:hover
{
    opacity:1;
    background-color: rgba(255, 255, 255, 0.1);
        box-shadow: 0 0 5px black;
    
}
#files .filename
{
        width: auto;
}
#video-line-content > a 
{
    overflow: hidden;
    width: 64px;
    height: 40px;
}
ul.video-gallery li.video-last
{
    background-color:  rgba(128, 128, 128, 0.2);;
    border: 1px solid lightgreen;
    /*    box-shadow: 0px 0px 20px 4px #d7f6a9;*/
}
div.block-side-extra, .mejs-contextmenu
{
        padding: 0;
}
div.user-actions a
{
        padding: 10px;
}
#block-side-control > div > div > div, #content-sidebar > div.block-side-extra > div > div
{
        margin: 0;
}
#video-line
{
 	border-radius: 0 0 10px 10px;
    border: 1px solid black;
    border-top: 0;   
    padding: 0px; 
}
#block-main-content > table > tbody > tr > td > img
{
        background-color: white;
    border-radius: 100%;
}
.tree-item{
    margin: 5px;
    padding: 5px;
}
#video-gallery-converting > ul > li > div > a > div.image 
{
        overflow:hidden;
        width: 180px;
    	height: 110px;
}
ul.video-gallery li div.image > img, #video-line img, ul.people-gallery-small li img,ul.channel-gallery-small li div.image, #video-gallery-converting > ul > li > div > a > div.image > img
{
        transition: transform 0.2s ease;

}
ul.video-gallery li:hover div.image > img , #video-line img:hover, ul.people-gallery-small li:hover img, ul.channel-gallery-small li:hover div.image, #video-gallery-converting > ul > li:hover > div > a > div.image > img
{
    transform: scale(1.1);
}

ul.video-gallery li > div > a:visited > div.text, #news-block > div > a:visited > div.heading, #video-index-right > div > a:visited, #video-side > div > ul > li> a:visited, #video-side > div > ul > li > div > a:visited
{
    opacity:0.7;
        color: #b6b6b6  !important;
}

#news-block
{    display: block;    
    font-size: larger;
   
    border-radius:10px;
}
#news-block > div > a:visited > div.heading
{
}
 #video-line{
      overflow: hidden;
}
    
#news .post{  
	padding-bottom: 5px;
    max-width: unset;
    border-bottom: 1px dotted lightskyblue;
}
#news-block > div > a > div.thumb
{
        display: none;
}
#news-block > div > a > div.heading, #news-block > div > a
{
    color: green;
}
#updates > li> ul > li
{
    height:0;
    transition: height 1s ease;
}
#updates > li> ul > li:nth-child(-n+3)
{
    height:unset;
    opacity:1;
    transform: translateY(0);
    z-index: 10;
}
#updates > li> ul > li:nth-child(4)
{
    height:1rem;
}
#updates > li> ul > li:nth-child(4):before
{
   content:"●●●";
}
#updates > li:hover> ul > li
{
    height:unset;
    transform: translateY(0);
    opacity:1;    
}
a.button-i.button-block,a.button-ok.button-block, #content-sidebar > div.block-side-extra > div > a, input[type="submit"]
{
  cursor: pointer;
  outline: none;
  border: none;
  box-shadow: 0 2px #999;
}
a.button-i.button-block:active,a.button-ok.button-block:active, div.block-side-extra > div > a:active, input[type="submit"]:active
{
    box-shadow: 0 1px #666;
    transform: translateY(2px);
}
}
