css:
@-moz-document domain("www.hi-pda.com") {
#postlist tbody td.postcontent.postbottom {
    display: none;
}
#postlist tbody > tr:nth-child(3) {
    display: none;
}
#postlist tbody .defaultpost {
    padding: 0.5em 0;
}
#postlist .postauthor > *:not(:first-child) {
    display: none;
}
#postlist .postauthor .postinfo {
    margin: 0;
}

#postlist .postcontent .postinfo {
    display: none;
}
#postlist .postcontent .postmessage .t_msgfontfix {
    min-height: initial;
}
}
