css:
@-moz-document url-prefix("https://www.otofun.net") {
/**
* Chào các cụ, các thím  :)
*/

html {
    background: #201F25;
    color: #FFFFFF;
}
#loginBar, #loginBar .pageContent {
    background-color: #201F25!important
}
body {
    font-family: verdana
}
.pageContent, .PanelScroller .panel, .PanelScrollerOff .panel {
    background-color: #F2F2F2!important
}
.nodeInfo.forumNodeInfo.primaryContent, .node .nodeLastPost, body .itemPageNav a, .PageNav a, .primaryContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .sectionMain, .nodeList .linkNodeInfo {
    background-color: #F5F5FF!important
}
.nodeList .categoryStrip, .listHeader, .sectionFooter, .bbCodeQuote .attribution, .tabs.prefixTabs {
    background: #869BBF url(https://vozforums.com/images/gradients/gradient_tcat.gif) repeat-x top left!important;
}
.listHeader, .tabs {
    border-bottom: 1px solid #B0BDC6;
}
.nodeInfo.primaryContent, .nodeInfo.linkNodeInfo, .discussionListItem, .section.modsList {
    border: 1px solid #B0BDC6;
    border-top: none
}
.nodeList .categoryStrip .nodeTitle a, .discussionList .sectionHeaders a, .nodeList .categoryStrip .nodeDescription, .sectionFooter, .messageContent a, .messageText a, .bbCodeQuote .attribution, .messageHead, a.headPermalink, .bbCodeQuote .attribution, .nodeList .categoryStrip .nodeTitle, #DiscussionListOptionsHandle a, .heading, .subHeading {
    color: white!important
}
.nodeList .nodeTitle a, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3, .nodeList .secondaryContent a, .discussionListItem .title a, body .itemPageNav a, .discussionListItem .stats .major, .discussionListItem .lastPostInfo .username, .PageNav a, .username .style2, .sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a, .xenOverlay.memberCard .userInfo h3 a, .xenOverlay.memberCard .userLinks, .tabs.prefixTabs li.active a, .message .publicControls .MultiQuoteControl.active {
    color: #23497C!important
}
body .itemPageNav a, .PageNav a, .messageList .message, div#ClassifiedContainer .sectionMain.xenForm, .tabs li a, .tabs.noLinks li {
    border: 1px solid #B0BDC6;
}
.message .messageInfo, .xenForm fieldset, .xenForm .formGroup {
    border-top: 1px solid #B0BDC6;
}
.PageNav {
    font-size: 10px
}
.PageNav a.currentPage {
    border-color: #3f3565
}
#copyright, .PageNav a.currentPage {
    color: black
}
.messageHead, a.headPermalink, .navigationSideBar .heading, .subHeading, .footer .pageContent, .discussionList .sectionHeaders {
    background: #5C7097 url(https://vozforums.com/images/gradients/gradient_thead.gif) repeat-x top left;
}
.avatar img, .avatar .img, .avatarCropper, .heading.ToggleTrigger {
    border: none
}
.secondaryContent {
    border-radius: 0!important;
}
.bbCodeQuote .quoteContainer, .tabs.prefixTabs li.active a, .messageUserInfo, .secondaryContent, .tabs.prefixTabs li.active a:hover, li[class^="redactor_btn_container"] [class^="redactor_btn"]:hover, .redactor_dropdown {
    background-color: #e1e4f2
}
.bbCodeQuote .quoteContainer .quoteExpand {
    background: linear-gradient(to bottom, rgba(212, 175, 110, 0) 0%, #d6c8ff 80%);
    color: black
}
.messageContent a:not(.AttributionLink), .messageText a:not(.AttributionLink) {
    color: gray!important
}
.messageText a:hover {
    color: #ea7a03!important
}
.button.primary, a.callToAction, #DiscussionListOptionsHandle a, .button.primary:focus {
    border-color: #5d79ad;
    background-color: #5d79ad;
}
.button.primary:hover, a.callToAction:hover, #DiscussionListOptionsHandle a:hover, .tabs li.active a, .tabs.noLinks li.active {
    border-color: #264a8e;
    background-color: #264a8e;
}
.PageNav a:hover, body .itemPageNav a:hover {
    border-color: #6366c5
}
#back2Top, .tabs li.active a {
    color: white!important
}
.navigationSideBar .secondaryContent:hover {
    background: #9caeff
}
.Menu .sectionFooter {
    background: white!important
}
textarea.textCtrl:focus, .textCtrl:focus, .textCtrl.Focus {
    background: white
}
.xenPreviewTooltip .arrow span {
   border-top-color: #5d79ad
}
.xenOverlay .formOverlay .textCtrl:focus {
    background-color: #04051b
}
#loginBar #loginBarHandle {
    background-color: #34365f
}
#loginBar, #loginBar #loginBarHandle {
    border-color: #35476f
}
/*[[navbar]]*/
}
