css:
@-moz-document domain(vk.com) {

/* Инвертация */
body {
	background-color: #12110f;
	filter: invert(100%);
}

/* Обратная инвертация */

/*[[pDI]]*/

/*[[tiDI]]*/

/* Что-то вроде водяного знака :) */
html:before {
	content: 'Zetta VK Inverted';
	/* Позиционирование */
	position: fixed;
	bottom: 0;
	left: 0;
	/* Color */
	color: white;
	text-shadow: 0 0 3px white;
	opacity: 0.3;
	font-family: monospace, "Ubuntu Mono";
	font-size: 12px;
}

}
