css:
@-moz-document domain("www.gismeteo.ru"), domain("www.gismeteo.by"), domain("www.gismeteo.ua"), domain("www.gismeteo.kz") {
body > *.media_top,
body > section yatag[id],
body > *:nth-child(2),
body > section > * > * > *:nth-child(3) > *,
body > section *.forecast_frame + section + *,
body > section > * > * > * > * > * > *.forecast_frame + * + * *[id],
body > aside > * > * > *:nth-child(2) > *,
body > aside > * > * > *:nth-child(3) > *,
body > aside *.forecast_frame + section + *,
body > aside > * > * > * > * > * > *.forecast_frame + * + * *[id],
body > aside > * > * > * > * > *.pageinfo + * + * *[id],
body > section > div.content_wrap > div.clearfix > .right_col_1,
body > section > div.content_wrap > div.clearfix > .right_col_2,
body.criteo #Page .ad_topper_cont {
    display: none !important;    
}
body *.body-clouds,
body > section div.clearfix > div {
    display: block !important;
}
*.items_cols.vesti,
*.extra.video,
*[data-media-middle2],
*.ad,
*.side,
*.wrap + * {
    display: none !important;
}
}

@-moz-document regexp("https://www\\.gismeteo\\.(ru|by|ua|kz)/maps/.*") {
body > section.content,
body > section > * > * > *:nth-child(2),
body > section > * > * > *:nth-child(2) > *
{
    display: block !important;
}
}
