css:
@-moz-document regexp("https://www.youtube.com/.*") {
canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form
{
    background: #1d1d1d
}

.yt-card
{
 background: #1d1d1d   
}
span
{ color: #FFFFFF;
    
}
hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, strike, strong, sub
{
    background: #1d1d1d;
    color: #FFFFFF !important;
         border-bottom-color: #FFFFFF                                       
}
.yt-uix-button img, .yt-uix-button-has-icon.no-icon-markup .yt-uix-button-content, .yt-uix-button-icon-wrapper+.yt-uix-button-content, span.yt-view-count-renderer
{ color: #FFFF00 !important}
#content-text.ytd-comment-renderer, .title.ytd-video-primary-info-renderer, .content.ytd-video-secondary-info-renderer, .more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer, .date.ytd-video-secondary-info-renderer
{ color: #FFFFFF
}
.count-text.ytd-comments-header-renderer, #icon-label.yt-dropdown-menu, .input-content.paper-input-container  textarea, ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox
{ color: #FFFFFF !important
}
ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox
{ background: #ffffff !important }
button

    {
    background: #1d1d1d 
    }
.video-list-item .title, #byline.ytd-video-meta-block
{
color: #FFFFFF !important }
a, abbr, acronym, address, applet, b, big, blockquote
{ background: #1d1d1d }
a, #video-title.ytd-compact-video-renderer
{ color: #FFFFFF !important}
#watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover
{ background: #FFFFFF }
#video-title.ytd-compact-radio-renderer
{ color: #FFFF00 } 
.title.ytd-video-primary-info-renderer, element.style 
{ color: #FFFF00 !important }
}
