css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {
#miniad_animation_container, .dying-links, #canvasnjuback {
    display: none !important;
}

.grid-right {
    display: none !important;
}

.article.clearfix.preview {
    display: none;
}
.article.clearfix.preview.dC {
    display: block;
}
.grid-main {
    margin-right: 0px !important;;
}

#nav li {
    display: block !important;
}

#add-new div {
    display: block;
}

.color-0.affect {
    color: #339933 !important;
}

.color-1.affect {
    color: #ff5917 !important;
}

.color-2.affect {
    color: #BB0000 !important;
}

#site > .wrapper > .grid > .grid-main > #itemsStream > .entry.iC.single.replyOn > .wblock.lcontrast.dC {
    display:block !important;
}

.author > .vC {
    display: /*[[optionhide]]*/
}

.blacklisted {
    display: /*[[banoption]]*/
}

#site > .wrapper > .grid > .grid-main > #itemsStream > .entry > .wblock.lcontrast.dC {
    display: /*[[mikroblog]]*/
}

.hidden-comment {
    display: /*[[hiddencomment]]*/
}

.stm-ad-player {
    display:none !important;
}

a[href*="adverts"] {
    display: none !important;
}

.cookie {
    display: none !important;
}

#site > span {
    display: none !important;
}

.link .empty-media {
    display: none !important;
}

.wblock > span {
    display: none !important;
}

}
