@-moz-document domain("web.roblox.com"), domain("roblox.com"), domain("forum.roblox.com") {
/*Feel free to add your own code in another section


/*Removes the Ad's content*/
a.ad img {
    display: none
}

/* Below is just to get rid of all of the pesky "Advertisment" text and "Report Ad" button, because the advertisment doesn't exist anymore*/
.abp .ad-identification-text {
    display: none
}
.abp .UpsellAdButton {
    display: none
}
.abp .UpsellAdButton:link {
    display: none
}

.abp .UpsellAdButton:visited {
    display: none
}
.abp .BadAdButton {
    display: none
}

.abp .BadAdButton:link {
    display: none
}
.abp .BadAdButton:visited {
    display: none
}
}
