    background: var(--fg-link) !important;
  }
  ::selection {
    color: var(--bg) !important;
    background: var(--fg-link) !important;
  }
  
body, .content_section, .module.platforms_module, .secondary_nav_table, #main_content, .toggle_tab.selected, .grades td.conversion, .settings_page .border_box
