    input[type="text"], input[type="password"], select, textarea, .see_all.boxed, .page_nav .page_num, .form_area .input_wrapper, .login_page #login, .user_input_disabled .login_msg, .link_tabs .link_tab, .styled_select.dark_grey, form.search .search_wrap, form.search .search_fields, form.search input.text, .adv_search .search_query .field, .adv_search input.text, .type_and_counts .count
