    .module .head_type_1 .tab .active, .module .head_type_1 .tab .active span, .toggles, .slider_arrow, .list_trailers .play_action, .list_trailers .play_action span, .list_trailers .play_action span a, .styled_select.opened .option.selected, .styled_select.opened .option:hover, .primary_dditems a:hover, #primary_menu_item_create, #primary_menu_item_create a
  { background-color: var(--fg-link); color: var(--bg) }
    .styled_select2, .social_round i
  { background-color: var(--fg-2) }
    .carousel_module .head .tab .active, .module.product_score_summary
  { background-color: transparent }
    body, input[type="text"], select, textarea, .new_releases_module .body, .module .head_type_1, .new_releases_module div.platforms, .module .body div.tabs, .module .tab .active span, .module .tab .active, .genre_nav .gn_wrap, .main_col .products_module .foot, .list_products .more_stats li.stat, .coming_soon_module, li.stickied_story .story_wrap, .list_trailers .play_action, .list_trailers .play_action span, .module .letternav, .carousel_slot span.product_wrap, .main_col .reviews_module .review_top, .main_col .reviews_module .review_btm, .reviews_module .score_details, .adv_search .search_query, .adv_search .search_query .field_wrap, #articlebody h3.special
  { background-image: none }
    .body
  { background-image: none !important }
    .slider_gradient.slider_next.light
  { background-image: linear-gradient(to left, var(--bg), rgba(0, 0, 0, 0)) }
    .slider_gradient.slider_previous.light
  { background-image: linear-gradient(to right, var(--bg), rgba(0, 0, 0, 0)) }

