    h1, h2, h3, h4, h5, .primary_menu_text, .current_platforms .platforms_label, .new_releases_module .more_new_releases .list_label, #primary_search .fa-search, .primary_menu_item.selected a, .label, .mc_yellow, .promo_amazon_wrapper .header, .strip_title:not(a), .hpb_item_title span, .content_head .product_seasons, .list_trailers .now_playing, #articlebody h2.special, .search_header .search_form_header, .clamp-list-main-title, .browse-list-heading, .title_bump .section_title
