    a, a:link, .promo_amazon .title, li.stickied_story .story_title a, .page_nav .page_flipper .flipper a .text, .module .head_type_2 .tab a, .module .head_type_2 .tab a:link, .module .head_type_2 .tab a:visited, .content_head .product_seasons .data a, .list_product_titles_ranked .product_title a, .see_all, #articlebody a, .resend_confirm
