    a:hover, a:visited:hover, #page:not(.iPad) a:hover, .promo_amazon .title:hover, li.stickied_story .story_title a:hover, .hpb_item_title span:hover, .content_head .product_seasons .data a:hover, .list_product_titles_ranked .product_title a:hover, .see_all:hover, #page:not(.iPad) .slide0 .gallery_stub_seeAll:hover, .resend_confirm:hover
