  { color: var(--fg-link-visited) }
    input
  { color: var(--fg-input) }
    .social_round i
  { color: #fff }
    .primary_nav_item .cutout_wrapper
  { color: var(--bg-2) }
    .module .-mvsor_hmds_5, [class="i10c-mark-cssrule"]
  { color: #000 }
  
