  .textscore_favorable { color: #3f3 }
  .metascore_w { text-shadow: 0 0 2px rgba(0, 0, 0, 1) }

    noborder, .navbar, .new_releases .product_basics, .body, .module, .list_products div.product_image, li.stickied_story .story_wrap, .login_page .border_box, #metatcritic_signup_form .border_box, .releases tfoot td, .critic_user_reviews .critic_user_reviews_wrap, .link_tabs .link_tab.selected, #articlebody a.button, #articlebody tr.even td, #articlebody a.button:hover, .comments p.signin, #articlebody h3.special, #articlebody table.linedtable td, #articlebody tbody thead th, .popStandardMsg, #hover_div
  { border: none }
