  { box-shadow: 2px 2px 4px #000 }
    .module.platforms_module, .module .module, .module.product_score_summary
  { box-shadow: none }
    custom-border, .login_page #login
  { border: var(--border) }
    input, select, textarea, div.supplements, .new_releases_module div.platforms, .list_products li.product, .module .head_type_2, .new_releases_module div.more_new_releases, .list_trailers .foot_wrap .more, .image_strip .title_wrapper, .promo_amazon_wrapper .header, .promo_amazon_wrapper.promo2, .page_nav .active_page .page_num, .page_nav .page_num, .stories li.story, .form_area .input_wrapper, .trailer_list_wrapper .see_all, .product_groups_module .foot, .top_features3, .top_releases_left th, .top_releases_left td.score_col, .top_releases_left td.divider, .carousel_slot div.slot_products, .user_input_disabled .login_msg, .product_reviews .user_review div.review_actions, .see_all.boxed, .browse .browse_list_wrapper table.list td, .browse .browse_list_wrapper.two table.list, .browse .browse_list_wrapper.three table.list, .two_w_separ th, .about_nav, .about .faq h1, .scores th, .scores td, .grades th, .grades td, .result, form.search .search_wrap, .adv_search li.fieldset, .adv_search .search_term, .adv_search .search_query .field, #articlebody table.bordertable td, #articlebody table.bordertable td table.linedtable td, #articlebody table.bordertable td, .comments .comment .wrap, .rss_nav ul.content_nav, table.credits th, .person_title, #primary_search .rounded_field
