    .toggle_expand_collapse::after
  { border-top-color: var(--fg-link) }
    .toggle_expand_collapse.toggle_collapse::after, #primary_search_results .results .item:last-of-type
  { border-bottom-color: var(--fg-link) }
    #primary_menu_item_create
  { border-color: transparent }

    .genre_nav .gn_wrap, .list_trailers .play_action, .list_trailers .play_action span
  { border-radius: 10px }
  
    success, success a
  { background-color: var(--bg-success); color: var(--fg-success) }
    info, info a
  { background-color: var(--bg-info);    color: var(--fg-info) }
    warning, warning a, .module.list_rankings .head, .module.list_rankings .head h2
  { background-color: var(--bg-warning); color: var(--fg-warning) }
    error, error a, .reviews_module div.msg_no_reviews p
  { background-color: var(--bg-error);   color: var(--fg-error) }
    disabled
  { background-color: var(--bg-disabled);   color: var(--fg-disabled) }
  .sharing-reddit { background-color: #777 } .sharing-reddit:hover { background-color: #999 }

    pre
  { background-color: var(--syntax-bg); border: none }
    pre
  { color: var(--syntax-fg) }

    #hover_div .hover_arrow, .cert_rating_img, .page_nav .page_flipper .flipper a, .page_nav .page_flipper .flipper a .text, .form_area .input_wrapper.locked_icon, img.rarrow
  { filter: invert(100%) }
    img[src*="/new.gif"]
  { filter: hue-rotate(60deg) saturate(200%) }
  
  .genre_nav .gn_wrap { padding-top: 10px }
  .styled_select2 i { -webkit-text-stroke-color: transparent }
  
    .mc_social_profiles, .stories_rss_feed_link, #providers, .social_round, .follow_like_module
  { display: none }
  
    .body_wrap
