@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.google.co.jp/"), url("https://www.google.com/"), url-prefix("https://www.google.co.jp/?"), url-prefix("https://www.google.co.jp/webhp?"), url-prefix("https://www.google.co.jp/search?"), url-prefix("https://www.google.com/?"), url-prefix("https://www.google.com/webhp?"), url-prefix("https://www.google.com/search?") {
#hplogo,
#logo img,
.logo img,
#prm-pt,
[name="btnI"] div[role],
.szppmdbYutt__middle-slot-promo {
  display: none !important;
}

#lga {
  margin-top: 89px !important;
  height: 233px !important;
  background-image: url(https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png) !important;
  background-size: 272px 92px !important;
  background-repeat: no-repeat !important;
  background-position: center 109px !important;
}

#logo,
.logo a{
  display: block !important;
  width: 92px !important;
  height: 33.5px !important;
  background-image: url(https://www.google.com/images/branding/googlelogo/2x/googlelogo_color_92x30dp.png) !important;
  background-size: 92px 30px !important;
  background-repeat: no-repeat !important;
  background-position: center top !important;
}
}

@-moz-document url-prefix("https://www.google.co.jp/"), url-prefix("https://www.google.com/") {
/* Googleの利用規約が改定されます */

.og-pdp,
#promos {
  animation: anime 2s linear 4s 1 normal forwards;
}

@keyframes anime {
  from { transform: translateY(0px); }
  to   { transform: translateY(-30px); }
}
}
