css:
@-moz-document domain("wormstorysearch.com") {
:root
{
    --bg-base: #090909;
    --bg-highlight: #0d0d0d;
    --fg-base: #999;
    --fg-highlight: #bbb;
    --cc-base: "/*[[colorbase]]*/";
    --cc-highlight: "/*[[colorhighlight]]*/";
    --cc-linkcolor:  "/*[[colorlink]]*/";
}

body
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    border-color: var(--fg-base);
}

.page-title,
hr
{
    border-color: var(--fg-base);
}

.table > tbody > tr.filters:hover,
.table > tbody > tr.sorters:hover
{
    background-color: initial;
}

.table > tbody > tr:not([class="filters"]):not([class="sorters"]):not([class="footer"]):hover
{
    background-color: var(--bg-highlight);
}

.table,
.table > tbody > tr > td,
.table > tbody + tbody
{
    border-color: var(--fg-base);
}

tbody > tr.info > td
{
    background-color: var(--bg-base) !important;
}

tbody > tr.info:hover > td
{
    background-color: var(--bg-highlight) !important;
}

.form-control
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    border-color: var(--fg-base);
}

.form-control:focus
{
    border-color: var(--cc-linkcolor);
    /*box-shadow: 0 0 8px var(--cc-highlight);*/
}

a
{
    color: var(--cc-linkcolor);
}

a:hover
{
    color: var(--cc-highlight);
}

a:visited
{
    color: var(--cc-highlight);
}

a .label
{
    color: var(--fg-highlight);
    background-color: var(--cc-base);
    border-radius: 2px;
    padding: 2px 4px;
}

a .label:hover
{
    color: var(--fg-highlight);
    background-color: var(--cc-highlight);
}

a.text-default
{
    color: var(--fg-base);
}

.title br
{
    margin-bottom: 3px;
}

.btn
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    border-color: var(--fg-base);
}

.btn:focus
{
    color: inherit;
    background-color: inherit;
    border-color: inherit;
}

.btn:hover,
.btn:focus:hover
{
    color: var(--fg-highlight);
    background-color: var(--bg-highlight);
    border-color: var(--fg-base);
}

.btn-primary,
.open > .btn-default.dropdown-toggle[aria-expanded="true"]
{
    color: var(--fg-base);
    background-color: var(--cc-base);
    border-color: var(--fg-base);
}

.btn-primary:focus,
.open > .btn-default.dropdown-toggle[aria-expanded="true"]:focus
{
    color: var(--fg-highlight);
    background-color: var(--cc-base);
    border-color: var(--fg-base);
}

.btn-primary:hover,
.btn-primary:focus:hover,
.open > .btn-default.dropdown-toggle[aria-expanded="true"]:hover,
.open > .btn-default.dropdown-toggle[aria-expanded="true"]:focus:hover
{
    color: var(--fg-highlight);
    background-color: var(--cc-highlight);
    border-color: var(--fg-base);
}

ul.dropdown-menu
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    border-color: var(--fg-base);
}

ul.dropdown-menu label
{
    color: var(--fg-base);
}

ul.dropdown-menu li a:hover
{
    background-color: var(--bg-highlight);
}

ul.dropdown-menu li.active > a
{
    background-color: var(--cc-base);
}

ul.dropdown-menu li.active > a:hover
{
    background-color: var(--cc-highlight);
}


/* PAGINATION */
.pagination
{
    margin: 0 !important;
}

.pagination li > a,
.pagination li > a:hover,
.pagination li > a:focus
{
    border-color: inherit !important;
}

.pagination li > a
{
    color: var(--fg-base);
    background-color: var(--bg-base);
}

.pagination li > a:hover,
.pagination li > a:focus
{
    color: var(--fg-base);
    background-color: var(--cc-highlight);
}

.pagination li.active > a
{
    color: var(--fg-base);
    background-color: var(--cc-base);
    border-color: inherit;
}

.pagination li.active > a:hover
{
    color: var(--fg-base);
    background-color: var(--cc-highlight);
    border-color: inherit;
}

.pagination li.disabled > a
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    cursor: default;
    pointer-events: none;
}

.pagination li.disabled > a:hover,
.pagination li.disabled > a:focus
{
    color: var(--fg-base);
    background-color: var(--bg-base);
    cursor: default;
    pointer-events: none;
}

.pagination > li:first-child > a,
.pagination > li:last-child > a
{
    border-radius: 2px;
}


/* MODAL */
.modal-dialog,
.modal-content,
.modal-content pre
{
    color: var(--fg-base);
    background-color: var(--bg-base);
}
}
