css:
@-moz-document domain('www.shinkunovels.com') {
	/*[[themecolor]]*/
  	
  	body hr {
      margin-top: 10px;
      margin-bottom: 10px;
      border: 0;
      border-top: 1px solid #ddd;
	}
  
    #main-header{
        padding: 0;
    }
    #container section#intro{
        display: none;
        opacity: 0;
    }
	#container .site-content .container {
		width: 100%;
	}
  	@media (min-width: 910px){
        #container .site-content .container {
            width: 880px !important;
        }
  	}
    #content{
        width: 100%;
    }
    #content article{
    	padding-left: 30px;
    	padding-right: 30px;
    }
    #content article .entry-content p{
        margin: 0;
        font-size: /*[[fontsize]]*/;
    }

    body #sidebar-secondary{
        width: 100%;
        text-align: center;
    }
}
