css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#tabbrowser-tabs,
#tabbrowser-arrowscrollbox,
#tabbrowser-tabs[positionpinnedtabs] > #tabbrowser-arrowscrollbox > .tabbrowser-tab[pinned] {min-height: 22px !important; max-height: 22px !important;}
#tabs-newtab-button .toolbarbutton-icon {width: 18px !important; height: 18px !important; padding: 2px !important;}
#new-tab-button .toolbarbutton-icon {width: 18px !important; height: 18px !important; padding: 2px !important;}
#navigator-toolbox[movingtab] > #titlebar > #TabsToolbar {padding-bottom: unset !important;}
#navigator-toolbox[movingtab] #tabbrowser-tabs {padding-bottom: unset !important; margin-bottom: unset !important;}
#navigator-toolbox[movingtab] > #nav-bar {margin-top: unset !important;}
.tab-content {padding: 0px 3px 0px 3px !important; font-family: Calibri !important;}
.tab-close-button {color: red !important;}
.tab-close-button:hover {fill-opacity: .4 !important;}
.tabs-alltabs-button {display: none !important;}
.tabbrowser-tab:not([pinned]) {min-width: 60px !important;}

#nav-bar-customization-target > toolbaritem {padding: 0 !important;}
